mapper.select:
        SELECT m.id,m.album,m.cover,m.lyric,m.name as musicName,s.name as singerName,m.url as musicUrl,m.online
        FROM music m  LEFT JOIN singer s ON  m.singerId=s.id
        WHERE 1=1

             
               
                 and  m.name LIKE #{nameStr}
             
             
                 
                 and s.name LIKE #{singerNameStr}
             
             
                 
                 and m.album LIKE #{albumStr}
             
        limit #{page},#{offSet}

  SELECT COUNT(m.id) FROM music m  LEFT JOIN singer s ON  m.singerId=s.id
WHERE 1=1
      
          
         AND  m.name LIKE #{nameStr}
      
      
          
          and s.name LIKE #{singerNameStr}
      
      
          
          and m.album LIKE #{albumStr}
