mapper.select:
        SELECT m.id,m.album,m.name as musicName, s.name as singerName,m.online,m.createTime,ss.realname as creator
        FROM music m
        LEFT JOIN singer s ON  m.singerId=s.id
        LEFT JOIN sysusers ss on m.creatorId=ss.id
        WHERE 1=1

             
               
                 and  m.name LIKE #{nameStr}
             
             
                 
                 and s.name LIKE #{singerNameStr}
             
             
                 
                 and m.album LIKE #{albumStr}
             
            
                and m.online=0
            
            
                and m.online=1
            
        ORDER BY m.id DESC ,m.online DESC

        limit #{page},#{offSet}

  SELECT COUNT(m.id) FROM music m
  LEFT JOIN singer s ON  m.singerId=s.id
WHERE 1=1
  
        
         AND  m.name LIKE #{nameStr}
  
  
          
          and s.name LIKE #{singerNameStr}
  
  
          
          and m.album LIKE #{albumStr}
  
  
      and m.online=0
  
  
      and m.online=1
